/**
 * @access private
 * This class is responsible for collecting delta changes in remote objects, before the response of subscription reach the observer.
 * It's used in Syncher -> subscribe. The flow is defined as:
 * 1. (observer) --subscribe--> (reporter)
 * 2. (observer) <--delta updates-- (reporter)
 * 3. (observer) <--subscribe response-- (reporter)
 * This means that there could be delta updates transferred before the subscription confirmation.
 * Since there is no DataObjectObserver before the ubscription confirmation, there should be some other object collecting the updates.
 * Provisional data is applied to the DataObjectObserver after confirmation. Or discarded if there is no confirmation.
 */
class DataProvisional {
  /* private
  _childrenListeners: [MsgListener]
  _listener: MsgListener

  _changes: []
  */

  constructor(owner, url, bus, children) {
    let _this = this;

    _this._owner = owner;
    _this._url = url;
    _this._bus = bus;
    _this._children = children;

    _this._changes = [];
    _this._allocateListeners();
  }

  _allocateListeners() {
    let _this = this;

    _this._listener = _this._bus.addListener(_this._url, (msg) => {
      console.log('DataProvisional-' + _this._url + '-RCV: ', msg);
      _this._changes.push(msg);
    });

    /*
    _this._childrenListeners = [];
    if (_this._children) {
      let childBaseURL = url + '/children/';
      _this._children.forEach((child) => {
        let childURL = childBaseURL + child;
        let listener = _this._bus.addListener(childURL, (msg) => {
          //ignore msg sent by himself
          if (msg.from !== owner) {
            console.log(msg);
          }
        });

        _this._childrenListeners.push(listener);
      });
    }*/
  }

  _releaseListeners() {
    let _this = this;

    _this._listener.remove();

    /*_this._childrenListeners.forEach((listener) => {
      listener.remove();
    });*/
  }

  get children() { return this._children; }

  apply(observer) {
    let _this = this;
    _this._changes.forEach((change) => {
      observer._changeObject(observer._syncObj, change);
    });
  }
}

export default DataProvisional;

import 'proxy-observe';

/**
 * @access private
 * Main class that maintains a JSON object, and observes changes in this object, recursively.
 * Internal objects and arrays are also observed.
 */
class SyncObject {

  constructor(initialData) {
    let _this = this;

    _this._observers = [];
    _this._filters = {};

    this._data = initialData || {};

    this._internalObserve(this._data);
  }

  get data() { return this._data; }

  observe(callback) {
    this._observers.push(callback);
  }

  find(path) {
    let list = path.split('.');

    return this._findWithSplit(list);
  }

  findBefore(path) {
    let result = {};
    let list = path.split('.');
    result.last = list.pop();
    result.obj = this._findWithSplit(list);

    return result;
  }

  _findWithSplit(list) {
    let obj = this._data;
    list.forEach((value) => {
      obj = obj[value];
    });

    return obj;
  }

  _isObservable(obj) {
    if (obj.constructor === Object || obj.constructor === Array) {
      return true;
    }

    return false;
  }


  _internalObserve(object) {

    let handler = (changeset) => {

      changeset.forEach((change) => {
        if (change.newValue && change.newValue instanceof Object) {
          if (change.newValue instanceof Object) {
            let o = Object.deepObserve(change.newValue, handler);
            object[change.keypath] = o;
          }

          if (change.newValue instanceof Array) {
            let o = Array.observe(change.newValue, handler);
            object[change.keypath] = o;
          }
        }
      });

      changeset.every((change) => {
        this._onChanges(change);
      });

    };

    this._data = Object.deepObserve(object, handler);

  }

  _fireEvent(event) {
    this._observers.forEach((callback) => {
      callback(event);
    });
  }

  _onChanges(change) {

    let obj = change.object;
    let objType;

    if (obj.constructor === Object) {
      objType = ObjectType.OBJECT;
    }

    if (obj.constructor === Array) {
      objType = ObjectType.ARRAY;
    }

    let fieldString = change.keypath;

    // console.log('Field:', fieldString);
    // console.log('type:', change.type);

    //let oldValue = change.oldValue;
    let newValue = obj[change.name];

    console.info(change.type + ' | Field: ' + fieldString + ' | New Value:', JSON.stringify(newValue));

    if (change.type === 'update') {
      this._fireEvent({
        cType: ChangeType.UPDATE,
        oType: objType,
        field: fieldString,
        data: newValue
      });
    }

    if (change.type === 'add') {
      this._fireEvent({
        cType: ChangeType.ADD,
        oType: objType,
        field: fieldString,
        data: newValue
      });
    }

    if (change.type === 'delete') {
      this._fireEvent({
        cType: ChangeType.REMOVE,
        oType: objType,
        field: fieldString
      });
    }
  }

}

export let ChangeType = {UPDATE: 'update', ADD: 'add', REMOVE: 'remove'};
export let ObjectType = {OBJECT: 'object', ARRAY: 'array'};
