    describe('createSourcePackage()', function () {
        let sourcePackage;

        it('should generate SourcePackage', function () {
            sourcePackage = catalogueDataObjectFactory.createSourcePackage("MyClassName", {});
            expect(sourcePackage).not.to.be.empty;
        });
    });

    describe('createCatalogueDataObject()', function () {

        let catalogueDataObject;

        it('should generate CatalogueDataObject of type HypertyDescriptor', function () {
            catalogueDataObject = catalogueDataObjectFactory.createCatalogueDataObject(
                "df7c7237-03e4-4547-89ca-c0c8b8d88f63", CatalogueObjectType.HYPERTY, "2.5.1", "My awesome Hyperty 4",
                "Description of Hyperty 4", DataObjectSourceLanguage.JAVASCRIPT_ECMA6,
                "https://example.org/my-awesome-hyperty/source");
            expect(catalogueDataObject).not.to.be.empty;
        });

        it('should be of type HYPERTY', function () {
            expect(catalogueDataObject.type).to.eql(CatalogueObjectType.HYPERTY);
        });

        it('should have valid GUID', function () {
            let guid = catalogueDataObject.guid;
            expect(guidCheck(guid)).to.be.true;
        });
    });

    describe('createHypertyRuntimeDescriptorObject()', function () {
        let hypertyRuntimeDescriptor;

        it('should generate HypertyRuntimeDescriptor', function () {
            hypertyRuntimeDescriptor = catalogueDataObjectFactory.createHypertyRuntimeDescriptorObject(
                "b36392c3-73d4-4a63-942b-4a9c2c663eea", "0.4.4", "My awesome Hyperty 5", "Description of Hyperty 5",
                DataObjectSourceLanguage.JAVASCRIPT_ECMA6, "https://example.org/my-awesome-hyperty-5/source",
                RuntimeType.BROWSER, new RuntimeHypertyCapability(true, true, true, false, false),
                new RuntimeProtocolCapability(true, true, true, true, false, true));
            expect(hypertyRuntimeDescriptor).not.to.be.empty;
        });

        it('should be of type HYPERTY_RUNTIME', function () {
            expect(hypertyRuntimeDescriptor.type).to.eql(CatalogueObjectType.HYPERTY_RUNTIME);
        });

        it('should have valid GUID', function () {
            let guid = hypertyRuntimeDescriptor.guid;
            expect(guidCheck(guid)).to.be.true;
        });
    });

    describe('createHypertyDataObjectSchema()', function () {
        let dataObjectSchema;

        it('should generate HypertyDataObjectSchema', function () {
            dataObjectSchema = catalogueDataObjectFactory.createHypertyDataObjectSchema(
                "b36392c3-73d4-4a63-942b-4a9c2c663eea", "1.2.1", "My awesome Schema",
                "Description of Schema",
                DataObjectSourceLanguage.JAVASCRIPT_ECMA6, "https://example.org/my-awesome-schema/source",
                "accessControlPolicyString", DataUrlScheme.COMM
            );
            expect(dataObjectSchema).not.to.be.empty;
        });


        it('should be of type DATA_SCHEMA', function () {
            expect(dataObjectSchema.type).to.eql(CatalogueObjectType.HYPERTY_DATA_OBJECT);
        });

        it('should have valid GUID', function () {
            let guid = dataObjectSchema.guid;
            expect(guidCheck(guid)).to.be.true;
        });
    });


    function guidCheck(guid) {
        if (typeof guid === "undefined") return false;
        else {
            //GUID should match standard RFC4122
            let match = guid.match(/^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i);
            return match !== null && match.length === 1;
        }
    }
});

import HypertyDiscovery from '../src/discovery/Discovery';

import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';

let expect = chai.expect;
chai.use(chaiAsPromised);

describe('Discovery', function() {
  let domain = 'ist.pt';
  let messageBus = {
    postMessage: (msg, replyCallback) => {

      //if the discoverHypertyPerUser don't receive a domain, it will use the domain from the constructor
      if (msg.body.resource === 'user://gmail.com/openidtest10') {
        expect(msg).to.eql({
          type: 'read', from: domain, to: 'domain://registry.ist.pt/',
          body: {resource: 'user://gmail.com/openidtest10'}
        });
        replyCallback({
          id: 1, type: 'response', to: msg.from, from: msg.to, body: {code: 200,
            assertedIdentity: 'user://gmail.com/openidtest10',
            value: {'hyperty://ist.pt/1':
                        {descriptor: 'hyperty-catalogue://ist.pt/.well-known/hyperty/HelloHyperty',
                         lastModified: '"2016-03-03T13:32:06Z"'}}}
        });
      } else if (msg.body.resource === 'user://specific.com/openidtest10') {
        expect(msg).to.eql({
          type: 'read', from: domain, to: 'domain://registry.specific.com/',
          body: {resource: 'user://specific.com/openidtest10'}
        });
        replyCallback({
          id: 1, type: 'response', to: msg.from, from: msg.to, body: {code: 200,
            assertedIdentity: 'user://specific.com/openidtest10',
            value: {'hyperty://specific.com/1':
                        {descriptor: 'hyperty-catalogue://specific.com/.well-known/hyperty/HelloHyperty',
                         lastModified: '"2016-03-03T13:32:06Z"'}}}
        });
      } else if (msg.body.resource === 'user://gmail.com/openidtest20') {
        expect(msg).to.eql({
          type: 'read', from: domain, to: 'domain://registry.ist.pt/',
          body: {resource: 'user://gmail.com/openidtest20', criteria: {resources: ['chat'], dataSchemes: ['comm']}}
        });
        replyCallback({
          id: 1, type: 'response', to: msg.from, from: msg.to, body: {code: 200,
            assertedIdentity: 'user://gmail.com/openidtest20',
            value: {'hyperty://ist.pt/1':
                        {descriptor: 'hyperty-catalogue://ist.pt/.well-known/hyperty/HelloHyperty',
                         lastModified: '"2016-03-03T13:32:06Z"',
                         dataSchemes: ['comm'],
                         resources:   ['chat']}}}
        });
      }

    }
  };

  let hypertyDiscovery = new HypertyDiscovery(domain, messageBus);

  describe('constructor()', function() {
    it('should create a HypertyDiscovery object without error', function() {
      expect(hypertyDiscovery.discoveryURL).to.be.equal(domain);
    });
  });

  describe('discoverHypertyPerUser()', function() {
    it('should return a Promise with an Identity using the defauld domain', function(done) {

      let expectedMessage = {id: 'openidtest10@gmail.com',
                            descriptor: 'hyperty-catalogue://ist.pt/.well-known/hyperty/HelloHyperty',
                            hypertyURL: 'hyperty://ist.pt/1'};

      expect(hypertyDiscovery.discoverHypertyPerUser('openidtest10@gmail.com').then(function(response) {
        return response;
      })).to.be.fulfilled.and.eventually.eql(expectedMessage).and.notify(done);

    });

    it('should return a Promise with an Identity using a given domain', function(done) {

      let expectedMessage = {id: 'openidtest10@specific.com',
                            descriptor: 'hyperty-catalogue://specific.com/.well-known/hyperty/HelloHyperty',
                            hypertyURL: 'hyperty://specific.com/1'};

      expect(hypertyDiscovery.discoverHypertyPerUser('openidtest10@specific.com', 'specific.com').then(function(response) {
        return response;
      })).to.be.fulfilled.and.eventually.eql(expectedMessage).and.notify(done);
    });
  });

  describe('discoverHyperty()', function() {
    it('should conclude the advanced search without error', function(done) {
      let expectedMessage = {'hyperty://ist.pt/1':
                      {descriptor: 'hyperty-catalogue://ist.pt/.well-known/hyperty/HelloHyperty',
                       lastModified: '"2016-03-03T13:32:06Z"',
                       dataSchemes: ['comm'],
                       resources:   ['chat']}
      };

      expect(hypertyDiscovery.discoverHyperty('user://gmail.com/openidtest20', ['comm'], ['chat']).then(function(response) {
        return response;
      })).to.be.fulfilled.and.eventually.eql(expectedMessage).and.notify(done);
    });
  });

});

import IdentityManager from '../src/identityManager/IdentityManager';

import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';

let expect = chai.expect;
chai.use(chaiAsPromised);

describe('IdentityManager', function() {
  let hypertyURL = 'hyperty://localhost/526e9670-593f-4641-8b63-98fa49f933a1';
  let runtimeURL = 'runtime://localhost/1051';
  let msgBus = {
    postMessage: (msg, replyCallback) => {

      expect(msg).to.eql({
        type: 'read', from: hypertyURL, to: runtimeURL + '/registry/',
        body: { resource: '.', criteria: hypertyURL}
      });

      replyCallback({
        type: 'read', from: runtimeURL + '/registry/', to: hypertyURL,
        body: { code: 200, resource: {cn: 'test OpenID', userURL: 'user://gmail.com/openidtest10', username: 'openidtest10@gmail.com', avatar: 'avatarURL'}}
      });
    }
  };

  let identityManager = new IdentityManager(hypertyURL, runtimeURL, msgBus);
  let expectedValue = {cn: 'test OpenID', userURL: 'user://gmail.com/openidtest10', username: 'openidtest10@gmail.com', avatar: 'avatarURL'};

  describe('constructor()', function() {
    it('should create a identityManager object without error', function() {
      expect(identityManager.runtimeURL).to.be.equal(runtimeURL);
    });
  });

  describe('discoverUserRegistered()', function() {
    it('should return a Promise with the identity associated', function(done) {

      expect(identityManager.discoverUserRegistered().then(function(response) {
        return response;
      })).to.be.fulfilled.and.eventually.eql(expectedValue).and.notify(done);
    });

    it('should return a Promise with the identity associated (with optional hyperty field)', function(done) {

      expect(identityManager.discoverUserRegistered('.', hypertyURL).then(function(response) {
        return response;
      })).to.be.fulfilled.and.eventually.eql(expectedValue).and.notify(done);
    });

  });

});

