import {RuntimeCatalogue} from '../src/RuntimeCatalogue';
import RuntimeFactory from './resources/RuntimeFactory';

import HypertyDescriptor from '../src/catalogue-factory/HypertyDescriptor';
import ProtocolStubDescriptor from '../src/catalogue-factory/ProtocolStubDescriptor';
