// Testing RuntimeCatalogue
describe('Runtime Catalogue', function () {
    let domain = 'localhost';
    let hypertyName = "FirstHyperty";
    let protostubName = "FirstProtostub";
    let runtimeName = "FirstRuntime";
    let schemaName = "FirstDataSchema";
    let idpproxyName = "FirstProxy";

    let runtimeFactory = new RuntimeFactory();

    let runtimeCatalogue = new RuntimeCatalogue(runtimeFactory);
    runtimeCatalogue.runtimeURL = domain;
    let tempHypertyDescriptor;

    it('should get hyperty descriptor', function (done) {
        let hypertyDescriptorURL = 'hyperty-catalogue://' + domain + '/.well-known/hyperty/' + hypertyName;
        expect(runtimeCatalogue.getHypertyDescriptor(hypertyDescriptorURL).then((hypertyDescriptor) => {
            //console.info("getHypertyDescriptor returned:", JSON.stringify(hypertyDescriptor, null, 2));
            tempHypertyDescriptor = hypertyDescriptor;
            return hypertyDescriptor;
        })).to.eventually.be.instanceof(HypertyDescriptor).and.notify(done);
    });

    it('should get sourcePackage', function (done) {
        expect(runtimeCatalogue.getSourcePackageFromURL(tempHypertyDescriptor.sourcePackageURL).then((sourcePackage) => {
            //console.info("getSourcePackageFromURL returned:", JSON.stringify(sourcePackage, null, 2));
            return sourcePackage;
        })).to.eventually.be.instanceof(SourcePackage).and.notify(done);
    });


    it('should get hyperty source code', function (done) {
        expect(runtimeCatalogue.getSourceCodeFromDescriptor(tempHypertyDescriptor).then((sourceCode) => {
            //console.info("getSourceCodeFromDescriptor returned:", sourceCode);
            return sourceCode;
        })).to.be.fulfilled.and.notify(done);

    });

    it('should get protostub descriptor', function (done) {
        let protostubURL = 'hyperty-catalogue://' + domain + '/.well-known/protocolstub/' + protostubName;
        expect(runtimeCatalogue.getStubDescriptor(protostubURL).then((protostub) => {
            //console.info("getStubDescriptor returned:", JSON.stringify(protostub, null, 2));
            return protostub;
        })).to.eventually.be.instanceof(ProtocolStubDescriptor).and.notify(done);
    });

    it('should get runtime descriptor', function (done) {
        let runtimeURL = 'hyperty-catalogue://' + domain + '/.well-known/runtime/' + runtimeName;
        expect(runtimeCatalogue.getRuntimeDescriptor(runtimeURL).then((runtime) => {
            //console.info("getRuntimeDescriptor returned:", JSON.stringify(runtime, null, 2));
            return runtime;
        })).to.eventually.be.instanceof(HypertyRuntimeDescriptor).and.notify(done);
    });

    it('should get dataschema descriptor', function (done) {
        let schemaURL = 'hyperty-catalogue://' + domain + '/.well-known/dataschema/' + schemaName;
        expect(runtimeCatalogue.getDataSchemaDescriptor(schemaURL).then((schemaDescriptor) => {
            //console.info("getDataSchemaDescriptor returned:", JSON.stringify(schemaDescriptor, null, 2));
            return schemaDescriptor;
        })).to.eventually.be.instanceof(DataObjectSchema).and.notify(done);
    });

    it('should get idpproxy descriptor', function (done) {
        let protostubURL = 'hyperty-catalogue://' + domain + '/.well-known/idp-proxy/' + idpproxyName;
        expect(runtimeCatalogue.getIdpProxyDescriptor(protostubURL).then((proxyDescriptor) => {
            //console.info("getIdpProxyDescriptor returned:", JSON.stringify(proxyDescriptor, null, 2));
            return proxyDescriptor;
        })).to.eventually.be.instanceof(ProtocolStubDescriptor).and.notify(done);
    });

    it('should NOT get hyperty', function (done) {
        let hypertyDescriptorURL = 'someInvalidURL';
        expect(runtimeCatalogue.getHypertyDescriptor(hypertyDescriptorURL).then((hypertyDescriptor) => {
            //console.info("getHypertyDescriptor returned:", JSON.stringify(hypertyDescriptor, null, 2));
            tempHypertyDescriptor = hypertyDescriptor;
            return hypertyDescriptor;
        })).to.be.rejected.and.notify(done);
    });

});

import chai from 'chai'
import Dexie from 'dexie'
import storageManager from '../src/storage-manager/StorageManager'

let expect = chai.expect
let storage
describe('StorageManager', function(){
	beforeEach(() => {
		const db = new Dexie('cache')
		const storeName = 'objects'

		storage = new storageManager(db, storeName)
	})

	describe('set', function(){

		it('should set the value for a given key-version tuple', function(done){
			storage.set('key', 'v1.0.0', {})
				.then( key => {
					expect(key).to.be.eql('key')
					done()
				})
		})

		it('should replace the value for a given key-version tuple if it exists', function(done){
			storage.set('key', 'v1.0.0', {})
				.then(() => storage.set('key', 'v1.0.0', {name: 'test'}))
				.then(() => storage.get('key'))
				.then( object => {
					expect(object).to.be.eql({name:'test'})
					done()
				})
		})
	})

	describe('get', function(){
		it('should get the value for a given key', function(done){
			storage.set('key', 'v1.0.0', {name:'test'})
				.then(() => {
					storage.get('key')
						.then( object => {
							expect(object).to.be.eql({name:'test'})
							done()
						})

				})
		})

		it('should return undefined if no object with the given key exists', function(done){
			storage.get('key123')
				.then( object => {
					expect(object).to.be.undefined
					done()
				})
		})
	})

	describe('getVersion', function(){
		it('should get the value version for a given key', function(done){
			storage.set('key', 'v1.0.0', {})
				.then(() => {
					storage.getVersion('key')
						.then(version => {
							expect(version).to.be.eql('v1.0.0')
							done()
						})
				})
		})

		it('should get undefined if no object with the given key exists', function(done){
			storage.getVersion('key123')
				.then(version => {
					expect(version).to.be.undefined
					done()
				})
		})
	})

	describe('delete', function(){
		it('should remove a value from StorageManager for a given key', function(done){
			storage.set('key', 'v1.0.0', {})
				.then(() => {
					storage.delete('key')
						.then(affected_records => {
							expect(affected_records).to.be.eql(1)
							done()
						})
				})
		})

		it('shouldnt remove a value from StorageManager if the given key doesnt exist', function(done){
			storage.delete('key321')
				.then(affected_records => {
					expect(affected_records).to.be.eql(0)
					done()
				})
		})
	})
})

import chai from 'chai';

import 'proxy-observe';
import SyncObject from '../src/syncher/ProxyObject';

chai.config.showDiff = true;
chai.config.truncateThreshold = 0;

let expect = chai.expect;

describe('The Object.Observer and Array.Observer', () => {

  it('should use SyncObject', () => {

    let initialData = {};
    let syncObj = new SyncObject(initialData);

    let data = syncObj.data;

    //apply changes...
    data['1'] = { name: 'Micael', birthdate: '28-02-1981', email: 'micael-xxx@gmail.com', phone: 911000000, obj1: {name: 'xpto'}};
    expect(data['1']).to.have.all.keys({name: 'Micael', birthdate: '28-02-1981', email: 'micael-xxx@gmail.com', phone: 911000000, obj1: {name: 'xpto'}});

    data['2'] = { name: 'Luis Duarte', birthdate: '02-12-1991', email: 'luis-xxx@gmail.com', phone: 910000000, obj1: { name: 'xpto' } };
    expect(data['2']).to.have.all.keys({name: 'Luis Duarte', birthdate: '02-12-1991', email: 'luis-xxx@gmail.com', phone: 910000000, obj1: { name: 'xpto' }});

    data['1'].name = 'Vitor Silva';
    expect(data['1']).to.deep.equal({ name: 'Vitor Silva', birthdate: '28-02-1981', email: 'micael-xxx@gmail.com', phone: 911000000, obj1: {name: 'xpto'}});

    data['1'].birthdate = new Date(1982, 1, 28);
    expect(data['1']).to.deep.equal({name: 'Vitor Silva', birthdate: new Date(1982, 1, 28), email: 'micael-xxx@gmail.com', phone: 911000000, obj1: {name: 'xpto'}});

    data['2'].obj1.name = 'XPTO NEW';
    expect(data['2']).to.deep.equal({name: 'Luis Duarte', birthdate: '02-12-1991', email: 'luis-xxx@gmail.com', phone: 910000000, obj1: { name: 'XPTO NEW' }});

    data['1'].obj1.name = 'Vitor Silva';
    expect(data['1']).to.deep.equal({name: 'Vitor Silva', birthdate: new Date(1982, 1, 28), email: 'micael-xxx@gmail.com', phone: 911000000, obj1: {name: 'Vitor Silva'}});

    data['1'].arr = [1, 0, { x: 10, y: 20 }];
    expect(data['1']).to.deep.equal({name: 'Vitor Silva', birthdate: new Date(1982, 1, 28), email: 'micael-xxx@gmail.com', phone: 911000000, obj1: {name: 'Vitor Silva'}, arr: [1, 0, { x: 10, y: 20 }]});

    data['1'].arr[1] = 2;
    expect(data['1'].arr[1]).to.deep.equal(2);

    delete data['2'];
    expect(data['2']).to.be.an('undefined');

  });

  it('should use SyncObject observe', (done) => {

    let initialData = {};
    let syncObj = new SyncObject(initialData);
    let seq = 0;

    syncObj.observe((event) => {
      seq++;

      console.log('seq ' + seq + ' | ' + ' - ' + JSON.stringify(event));

      if (seq === 1) {
        expect(event).to.deep.equal({cType: 'add', oType: 'object', field: '1', data: {name: 'Micael', birthdate: '28-02-1981', email: 'micael-xxx@gmail.com', phone: 911000000, obj1: {name: 'xpto'}, arr: []}});
      }

      if (seq === 2) {
        expect(event).to.deep.equal({cType: 'add', oType: 'object', field: '2', data: {name: 'Luis Duarte', birthdate: '02-12-1991', email: 'luis-xxx@gmail.com', phone: 910000000, obj1: { name: 'xpto' }}});
      }

      if (seq === 3) {
        expect(event).to.deep.equal({cType: 'update', oType: 'object', field: 'name', data: 'Vitor Silva'});
      }

      if (seq === 4) {
        expect(event).to.deep.equal({cType: 'update', oType: 'object', field: 'arr', data: [1, 0, { x: 10, y: 20 }]});
        done();
      }

      if (seq === 5) {
        expect(event).to.deep.equal({cType: 'update', oType: 'object', field: 'arr', data: [1, 0, { x: 10, y: 20 }]});
        // done();
      }

    });

    let data = syncObj.data;

    //apply changes...
    data['1'] = {name: 'Micael', birthdate: '28-02-1981', email: 'micael-xxx@gmail.com', phone: 911000000, obj1: {name: 'xpto'}, arr: []};
    data['2'] = {name: 'Luis Duarte', birthdate: '02-12-1991', email: 'luis-xxx@gmail.com', phone: 910000000, obj1: { name: 'xpto' }};
    data['1'].name = 'Vitor Silva';
    data['1'].arr = [1, 0, { x: 10, y: 20 }];

    // TODO wait for the ProxyObserve fix this issue; https://github.com/anywhichway/proxy-observe/issues/16
    data['1'].arr[1] = 2;

  });

});

// Unit test modules
import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';
import sinonChai from 'sinon-chai';

// internal modules to be tested
import {divideURL, convertToUserURL} from '../src/utils/utils';

let expect = chai.expect;

chai.config.showDiff = true;
chai.config.truncateThreshold = 0;

chai.use(chaiAsPromised);
chai.use(sinonChai);

/**
 * this is test for Utils.
 * @test {utils}
 */
describe('Utils Module', function() {

  it('should divide an url in 3 parts (type, domain, identity)', function() {

    // http://localhost:9090/test
    // hyperty://localhost:9090/test
    // http://sp1.sp/test-testes
    // hyperty-catalogue://sp1.sp/HelloHyperty
    // runtime://sp1.sp/8456/sm
    // domain://msg-node.sp1.sp/hyperty-address-allocation
    // hyperty-runtime://sp1.sp

    let url = 'runtime://sp.sp/8456/sm';
    expect(divideURL(url)).to.deep.equal({'type' : 'runtime','domain': 'sp.sp', 'identity': '/8456/sm'});

    url = 'domain://msg-node.sp1.sp/hyperty-address-allocation';
    expect(divideURL(url)).to.deep.equal({'type' : 'domain','domain': 'msg-node.sp1.sp', 'identity': '/hyperty-address-allocation'});

    url = 'hyperty://sp1.sp/HelloHyperty';
    expect(divideURL(url)).to.deep.equal({'type' : 'hyperty','domain': 'sp1.sp', 'identity': '/HelloHyperty'});

    url = 'hyperty://localhost:9090/test';
    expect(divideURL(url)).to.deep.equal({'type' : 'hyperty','domain': 'localhost:9090', 'identity': '/test'});

    url = 'runtime://sp1.sp';
    expect(divideURL(url)).to.deep.equal({'type' : 'runtime','domain': 'sp1.sp', 'identity': ''});

    url = 'http://localhost:9090/test';
    expect(divideURL(url)).to.deep.equal({'type' : 'http','domain': 'localhost:9090', 'identity': '/test'});

    url = 'https://localhost:9090/secure-test';
    expect(divideURL(url)).to.deep.equal({'type' : 'https','domain': 'localhost:9090', 'identity': '/secure-test'});
  });

  it('should convert a user identity to userURL', function() {

    let userIdentity = 'openidtest10@gmail.com';
    expect(convertToUserURL(userIdentity)).to.deep.equal('user://gmail.com/openidtest10');

    userIdentity = 'user://gmail.com/openidtest10';
    expect(convertToUserURL(userIdentity)).to.deep.equal('user://gmail.com/openidtest10');
  });
});
